import React, { useEffect } from "react";

const Home = () => {
	const [userName, setUserName] = useState("");

	useEffect(() => {
		// Get the user's name from localStorage or state
		const name = localStorage.getItem("userName");
		setUserName(name);
	}, []);

	return (
		<h2>Welcome, {userName ? userName : "Guest"}! Welcome to our main page.</h2>
	);
};

export default Home;
